mapper.select:
select n.notice_no as notice_no , n.member_type_no as member_type_no , mt.member_type as member_type , n.name as name , n.critical_check as critical_check , n.register_date as register_date from notice n left outer join member_type mt on n.member_type_no = mt.member_type_no where 1=1
and n.member_type_no = #{memberTypeNo}
order by notice_no desc LIMIT 10

mapper.update:
update notice set 
  view_count=view_count + 1 
where notice_no=#{no}
